package com.BasicProgram;

public class ForLoop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=0; i<=2; i++) {
			
			System.out.println("Class A" +i);
			
			for (int j=1;j<=3;j++) {
				
				System.out.println("Student" +j);
			}
		}

	}

}
